    });
  };


  useEffect(() => {
    let isMounted = true; // Track if component is mounted

    const fetchPost = async () => {
      try {
        const postResponse = await axios.get(
          `http://localhost:3001/posts/byId/${id}`
        );
        if (isMounted) {
          setPostObject(postResponse.data);
        }
      } catch (error) {
        console.error("Error fetching post:", error);
      }
    };

    const fetchComments = async () => {
      try {
        const commentsResponse = await axios.get(
          `http://localhost:3001/comments/${id}`
        );
        if (isMounted) {
          setComments(commentsResponse.data);
        }
      } catch (error) {
        console.error("Error fetching comments:", error);
      }
    };

    fetchPost();
    fetchComments();

    return () => {
      isMounted = false; // Cleanup on unmount
    };
  }, [id]); // Add dependency array with id

  return (
    <div className="postPage">
      <div className="leftSide">
        <div className="postTitle">{postObject.title}</div>
        <div className="postText">{postObject.postText}</div>
        <div className="footer">{postObject.username}</div>
      </div>
      <div className="rightSide">
        <div className="addCommentContainer">
          <input
            type="text"
            placeholder="Comment..."
            autoComplete="off"
          ></input>
          <button type="button" onClick={addComment}>Add Comment</button>
        </div>
        <div className="listOfComments">
          {comments.map((comment, key) => (
            <div key={key} className="comment">
              <div className="commentBody">{comment.commentBody}</div>
              <div className="commentFooter">{comment.username}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Post;
