import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Post() {
  let { id } = useParams();
  const [postObject, setPostObject] = useState({});
  const [comments, setComments] = useState({});
  const navigate = useNavigate();

  const addComment = (data) => {
    axios.post("http://localhost:3001/comments", data, {}).then((response) => {
      navigate(`/post/${id}`);
    });
  };



  useEffect(() => {
    let isMounted = true; // Track if component is mounted

    axios
      .get(`http://localhost:3001/posts/byId/${id}`)
      .then((response) => {
        if (isMounted) {
          setPostObject(response.data);
        }
      })
      .catch((error) => {
        console.error("Error fetching post:", error);
      });

    axios
      .get(`http://localhost:3001/comments/${id}`)
      .then((response) => {
        if (isMounted) {
          setComments(response.data);
        }
      })
      .catch((error) => {
        console.error("Error fetching comments:", error);
      });

    return () => {
      isMounted = false; // Cleanup on unmount
    };
  }, [id]); // Add dependency array with id

  return (
    <div className="postPage">
      <div className="leftSide">
        <div className="postTitle">{postObject.title}</div>
        <div className="postText">{postObject.postText}</div>
        <div className="footer">{postObject.username}</div>
      </div>
      <div className="rightSide">
        <div className="addCommentContainer">
          <input
            type="text"
            placeholder="Comment..."
            autoComplete="off"
          ></input>
          <button type="button">Add Comment</button>
        </div>
        <div className="listOfComments">
          {comments.map((comment, key) => {
            return <div className="title">{comment.commentBody}</div>;
          })}
        </div>
      </div>
    </div>
  );
}

export default Post;
